glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
attribute vec3 a_position;
void main () {
  gl_Position = cc_matViewProj * cc_matWorld * vec4(a_position, 1);
  gl_Position.z -= 0.0001;
}

glsl3.frag:
precision highp float;
vec4 CCFragOutput (vec4 color) {
  #if OUTPUT_TO_GAMMA
    color.rgb = sqrt(color.rgb);
  #endif
	return color;
}
uniform DIFFUSE_COLOR {
  vec4 diffuseColor;
};
vec4 front() {
  return CCFragOutput(diffuseColor);
}
out vec4 cc_FragColor;
void main() { cc_FragColor = front(); }

