glsl3.vert:
precision highp float;
in vec3 a_position;
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
uniform CC_SHADOW_MAP {
  mat4  cc_shadow_map_lightViewProjMatrix;
  vec4  cc_shadow_map_info;
  float cc_shadow_map_bias;
};
out float v_depth;
